module.exports = class Abort extends WebComponentAbstract {
  init() {
  
    var p = this.new('p')
    p.new('a', true, { textContent: 'Emergency stop', className: 'button' }).on('click', e => this.send('M112'))
    p.new('br')
    p.new('a', true, { textContent: 'Abort plannedÂ moves', className: 'button' }).on('click', e => this.send('M410'))
  }

  send(val) {
    self.emit('serialDirectWrite', { data: val + '\n' })
  }
}
