module.exports = class Fan extends WebComponentAbstract {
  init() {
    var p = this.new('p', { textContent: 'Fan ' })
    
    var slider = p.new('input', { type: 'range', min: 0, max: 255, step: 1, value: 0 })
    slider.on('input', e => this.setSpeed(slider.value))
  }
  
  setSpeed(val) {
    self.emit('serialWrite', { data: `M106 S${val}`, prepend: true })
  }
}
