module.exports = class Home extends WebComponentAbstract {
  init() {
    var p = this.new('p', { textContent: 'Home ' })
    
    p.new('a', { textContent: 'X', className: 'button' }).on('click', e => this.home('X'))
    p.newText(' ')
    p.new('a', { textContent: 'Y', className: 'button' }).on('click', e => this.home('Y'))
    p.newText(' ')
    p.new('a', { textContent: 'Z', className: 'button' }).on('click', e => this.home('Z'))
  }
  
  home(val) {
    self.emit('serialWrite', { data: 'G28 ' + val, prepend: true })
  }
}
