module.exports = class Laser extends WebComponentAbstract {
  init() {
    var p = this.new('p', { textContent: 'Laser ' })
    
    var slider = p.new('input', { type: 'range', min: 0, max: 255, step: 1, value: 0 })
    slider.on('input', e => this.setPower(slider.value))
  }
  
  setPower(val) {
    self.emit('serialWrite', { data: `M3 S${val}`, prepend: true })
  }
}
