module.exports = class PositionMemory extends WebComponentAbstract {
  init() {
    
    var p = this.new('p')
    p.new('a', { textContent: 'Save current coordinates', className: 'button' }).on('click', e => this.send('G60'))
    
    var p = this.new('p')
    p.newText('Restore saved coordinates')
    p.new('br')
    p.new('a', { textContent: 'X', className: 'button' }).on('click', e => this.send('G61 X'))
    p.newText(' ')
    p.new('a', { textContent: 'Y', className: 'button' }).on('click', e => this.send('G61 Y'))
    p.newText(' ')
    p.new('a', { textContent: 'Z', className: 'button' }).on('click', e => this.send('G61 Z'))
    p.newText(' ')
    p.new('a', { textContent: 'All', className: 'button' }).on('click', e => this.send('G61 XYZ'))
  }
  
  send(val) {
    self.emit('serialWrite', { data: val, prepend: true })
  }
}
