module.exports = class Power extends WebComponentAbstract {
  init() {
  
    var p = this.newElem('p', true, { textContent: 'Power ' })
    p.newElem('a', true, { textContent: 'On', className: 'button' }).on('click', e => this.send('M80'))
    p.newText(' ')
    p.newElem('a', true, { textContent: 'Off', className: 'button' }).on('click', e => this.send('M81'))
    
    p.newElem('br')
    p.newText('Motors ')
    p.newElem('a', true, { textContent: 'On', className: 'button' }).on('click', e => this.send('M17'))
    p.newText(' ')
    p.newElem('a', true, { textContent: 'Off', className: 'button' }).on('click', e => this.send('M18'))
  }

  send(val) {
    AppEvent('serialWrite', { data: val, prepend: true })
  }
}
