module.exports = class Power extends WebComponentAbstract {
  init() {
    var p = this.new('p', { textContent: 'Power ' })
    p.new('a', { textContent: 'On', className: 'button', tabIndex: 0 }).on('click', e => this.send('M80'))
    p.newText(' ')
    p.new('a', { textContent: 'Off', className: 'button', tabIndex: 0 }).on('click', e => this.send('M81'))
    
    p.new('br')
    p.newText('Motors ')
    p.new('a', { textContent: 'On', className: 'button', tabIndex: 0 }).on('click', e => this.send('M17'))
    p.newText(' ')
    p.new('a', { textContent: 'Off', className: 'button', tabIndex: 0 }).on('click', e => this.send('M18'))
  }

  send(val) {
    self.emit('serialWrite', { data: val, prepend: true })
  }
}
