module.exports = class RelativeAbsolute extends WebComponentAbstract {
  init() {
    
    var p = this.new('p', { textContent: 'Coordinates ' })
    p.new('a', { textContent: 'Absolute', className: 'button' }).on('click', e => this.send('G90'))
    p.newText(' ')
    p.new('a', { textContent: 'Relative', className: 'button' }).on('click', e => this.send('G91'))
    
    var p = this.new('p', { textContent: 'Extruder ' })
    p.new('a', { textContent: 'Absolute', className: 'button' }).on('click', e => this.send('M82'))
    p.newText(' ')
    p.new('a', { textContent: 'Relative', className: 'button' }).on('click', e => this.send('M83'))
  }
  
  send(val) {
    self.emit('serialWrite', { data: val, prepend: true })
  }
}
