module.exports = class Settings extends WebComponentAbstract {
  init() {
    
    var p = this.new('p', true, { textContent: 'Revert ' })
    p.new('a', true, { textContent: 'Saved settings', className: 'button' }).on('click', e => this.send('M501'))
    p.newText(' ')
    p.new('a', true, { textContent: 'Factory default', className: 'button' }).on('click', e => this.send('M502'))
    
    var p = this.new('p', true, { textContent: 'Current settings ' })
    p.new('a', true, { textContent: 'Save', className: 'button' }).on('click', e => this.send('M500'))
    p.newText(' ')
    p.new('a', true, { textContent: 'Read', className: 'button' }).on('click', e => this.send('M503'))
    
    self.on('serialEcho', e => this.echo(e.detail))
  }
  
  send(val) {
    self.emit('serialWrite', { data: val, prepend: true })
  }
  
  echo(msg) {
    if(msg.startsWith('Settings Stored')) self.emit('msg', 'Settings saved')
  }
}
