module.exports = class SpeedFactor extends WebComponentAbstract {
  init() {
    var p = this.new('p', { textContent: 'Speed ' })
    
    var slider = p.new('input', { type: 'range', min: 0, max: 100, step: 1, value: 100 })
    slider.on('input', e => this.setSpeed(slider.value))
  }
  
  setSpeed(val) {
    self.emit('serialWrite', { data: `M220 S${val}`, prepend: true })
  }
}
