var style = `
:host {
  order: -100;
  padding-left: 0.25rem;
  font-size: 0.85em;
}
`

module.exports = class SerialCount extends WebComponentAbstract {
  init() {
    this.renderCss(style)
    
    this.new('a', { textContent: 'Clean buffer' }).on('click', e => self.emit('serialClean'))
    this.newText(' | ')
    var count = this.newText()
    
    self.on('bufferChange', e => count.textContent = 'Buffer count ' + e.detail.length)
    
    self.emit('newPanel', { el: this, position: 'bottom' })
  }
}
