module.exports = class UI extends WebComponentAbstract {
  init() {
    // messaging system
    this.new(require('./Msg'), {}, false)
    
    // files dragging
    this.new(require('./Files'), {}, false)
    
    // prevent browser zoom in non developer mode
    this.developer = false
    self.on('developerMode', e => this.developer = true)
    self.on('wheel', e => e.ctrlKey && !this.developer && e.preventDefault())
    
    // emit click when space or enter press
    self.on('keypress', e => (e.keyCode == 13 || e.keyCode == 32) && document.activeElement.emit('click'))
  }
}
